import sys, PyPDF2, oswatermark_file = sys.argv[1]input_file = sys.argv[2]output = PyPDF2.PdfFileWriter()output_file_name = os.path.basename(input_file).split('.')[0]print(watermark_file)print(input_file)def watermark_pdf(input_file, watermark_file):    with open(input_file, 'rb') as file:         reader = PyPDF2.PdfFileReader(file)                with open(watermark_file, 'rb') as watermark:             watermark_page = PyPDF2.PdfFileReader(watermark).getPage(0)                        num = reader.getNumPages()            for i in range(num):                page = reader.getPage(i)                page.mergePage(watermark_page)                #page.mergePage(watermark.getPage(0))                #print(page)                output.addPage(page)                            with open(f"{output_file_name}_WM.pdf", "wb") as op_file:                output.write(op_file)                watermark_pdf(input_file, watermark_file)# Better# template = PyPDF2.PdfFileReader(open(input_file, 'rb'))# watermark = PyPDF2.PdfFileReader(open(watermark_file, 'rb'))# output = PyPDF2.PdfFileWriter()